function moveElementBeforeClass(elementToMove, target) {
  // ì˜®ê¸°ë ¤ëŠ” ìš”ì†Œë¥¼ ì°¾ìŠµë‹ˆë‹¤.
  const element = document.querySelector(elementToMove);
  if (!element) {
    console.error(`"${elementToMove}" ìš”ì†Œë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.`);
    return;
  }

  // íŠ¹ì • í´ë˜ìŠ¤ë¥¼ ê°€ì§„ ì²« ë²ˆì§¸ ìš”ì†Œë¥¼ ì°¾ìŠµë‹ˆë‹¤.
  const targetElement = document.querySelector(target);
  if (!targetElement) {
    console.error(`"${target}" ìš”ì†Œë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.`);
    return;
  }

  // ê¸°ì¤€ ìš”ì†Œì˜ ë¶€ëª¨ ë…¸ë“œë¥¼ ì–»ìŠµë‹ˆë‹¤.
  const parent = targetElement.parentNode;
  if (!parent) {
    console.error("ê¸°ì¤€ ìš”ì†Œì˜ ë¶€ëª¨ ë…¸ë“œë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.");
    return;
  }

  // ì˜®ê¸°ë ¤ëŠ” ìš”ì†Œë¥¼ ê¸°ì¤€ ìš”ì†Œì˜ ë°”ë¡œ ì•ì— ì‚½ì…í•©ë‹ˆë‹¤.
  parent.insertBefore(element, targetElement);
}

// ì‚¬ìš© ì˜ˆì‹œ:
// <div id="item-to-move">ì´ë™í•  ì•„ì´í…œ</div>
// <div class="target-class">ê¸°ì¤€ ì•„ì´í…œ 1</div>
// <div class="target-class">ê¸°ì¤€ ì•„ì´í…œ 2</div>

moveElementBeforeClass(".mw-editTools", ".editOptions");

/*
 * ì›ë³¸: https://www.mediawiki.org/wiki/MediaWiki:Gadget-Edittools.js
 */
console.log('EditTools ìŠ¤í¬ë¦½íŠ¸ ì‹œì‘');

(function (mw) {
  "use strict";

  var conf, editTools, sections;

  conf = {
    initialSubset: window.EditTools_initial_subset === undefined ? window.EditTools_initial_subset : 0
  };

  editTools = {
    /**
     * Creates the selector
     */
    setup: function () {
      var container, select, initial;
      container = document.getElementById('mw-edittools-charinsert');
      if (!container) {
        return;
      }

      sections = container.querySelectorAll('.mw-edittools-section');
      if (sections.length <= 1) {
        // Only care if there is more than one
        return;
      }

		// ê°ì‹¸ëŠ” div ìƒì„±
      var selectWrapper = document.createElement('div');
      selectWrapper.classList.add('oo-ui-widget', 'oo-ui-widget-enabled', 'oo-ui-inputWidget', 'oo-ui-dropdownInputWidget');

      select = document.createElement('select');
      select.style.display = 'inline';
      select.classList.add('oo-ui-inputWidget-input', 'oo-ui-indicator-down');

      initial = conf.initialSubset;
      if (isNaN(initial) || initial < 0 || initial >= sections.length) {
        initial = 0;
      }

      sections.forEach(function (section, i) {
        var sectionTitle, option;
        sectionTitle = section.dataset.sectionTitle;
        option = document.createElement('option');
        option.textContent = sectionTitle;
        option.value = i.toString();
        option.selected = (i === initial);
        select.appendChild(option);
      });

      select.addEventListener('change', editTools.handleOnchange);

      // select ìš”ì†Œë¥¼ ê°ì‹¸ëŠ” divì— ì¶”ê°€
      selectWrapper.appendChild(select);

      // ê°ì‹¸ëŠ” divë¥¼ containerì— prepend
      container.prepend(selectWrapper);
      editTools.chooseSection(initial);
    },

    /**
     * Handle onchange event of the <select>
     *
     * @param {Event} e
     */
    handleOnchange: function (e) {
      editTools.chooseSection(Number(e.target.value));
      return true;
    },

    /**
     * Toggle the currently visible section
     *
     * @param {Number} sectionNum
     */
    chooseSection: function (sectionNum) {
      var choice = sections[sectionNum];
      if (!choice) {
        return;
      }

      choice.style.display = 'inline';
      sections.forEach(function (section) {
        if (section !== choice) {
          section.style.display = 'none';
        }
      });
    }
  };

  $(function() {
    editTools.setup();
  });
}(mediaWiki));

